resources.string:
        PREFIX w3c: <http://www.w3.org/2000/01/rdf-schema#>
		PREFIX iot: <http://campus.guide/OpenIoT/owl#>
		SELECT ?label ?uri ?latitude ?longitude ?phone
		WHERE   {
			?uri  iot:label ?label  .
			OPTIONAL {
				?uri  iot:lat ?latitude ;
				      iot:long ?longitude .
			}
			OPTIONAL {
   				?uri iot:phone ?phone
			}
		}

	    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
		SELECT ?uri ?label  ?latitude ?longitude ?phone
		WHERE {
			<%s> iot:label ?label .
			?uri iot:label ?label .
			OPTIONAL {
				?uri  iot:lat ?latitude ;
				      iot:long ?longitude .
			}
			OPTIONAL {
   				?uri iot:phone ?phone
			}
		}

	    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
		PREFIX w3c: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
	    SELECT DISTINCT ?label ?uri ?latitude ?longitude ?phone
		WHERE   {
         %s
         OPTIONAL { ?uri iot:label ?label . }
         OPTIONAL {
			?uri  iot:lat ?latitude ;
			      iot:long ?longitude .
		 }
		 OPTIONAL {
  				?uri iot:phone ?phone
		 }
        }

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    SELECT
    	?room ?user ?startTime ?endTime ?appointmentWasKept
    WHERE
	{
		<%s>   iot:reservedRoom ?room ;
		             iot:fromUser ?user ;
			         iot:startTime ?startTime ;
			         iot:endTime ?endTime ;
			         iot:appointmentWasKept ?appointmentWasKept .
		OPTIONAL {
					   ?reservation iot:appointmentWasKept ?appointmentWasKept
		}
	}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    SELECT
    	?reservation ?room ?startTime ?endTime ?appointmentWasKept
    WHERE
	{
		?reservation iot:reservedRoom ?room ;
		             iot:fromUser <%s> ;
			         iot:startTime ?startTime ;
			         iot:endTime ?endTime ;
			         iot:appointmentWasKept ?appointmentWasKept .
		OPTIONAL {
					   ?reservation iot:appointmentWasKept ?appointmentWasKept
		}
	}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    SELECT
    	?reservation ?room ?startTime ?endTime ?appointmentWasKept
    WHERE
	{
		?reservation iot:reservedRoom <%s> ;
		             iot:fromUser ?user ;
			         iot:startTime ?startTime ;
			         iot:endTime ?endTime ;
			         iot:appointmentWasKept ?appointmentWasKept .
		OPTIONAL {
					   ?reservation iot:appointmentWasKept ?appointmentWasKept
		}
	}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    INSERT DATA
	{
		<%s> iot:reservedRoom <%s> ;
				   iot:fromUser <%s> ;
				   iot:startTime %d ;
				   iot:endTime %d ;
				   iot:appointmentWasKept false .
	}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    DELETE DATA
	{
		<%s> iot:reservedRoom ?room ;
				   iot:fromUser ?user ;
				   iot:startTime ?startTime ;
				   iot:endTime ?endTime ;
				   iot:appointmentWasKept ?appointmentWasKept .
	}

PREFIX iot: <http://campus.guide/OpenIoT/owl#>
ASK
{
	?reservation iot:reservedRoom <%s> ;
				 iot:startTime ?startTime ;
				 iot:endTime ?endTime .
	FILTER (
		(?endTime   > %d && ?endTime   < %d) || 
		(?startTime > %d && ?startTime < %d) ||
		(?startTime < %d && ?endTime   > %d) ||
		(?startTime > %d && ?endTime   < %d) 
	)
}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    INSERT DATA
	{
		<%s> iot:appointmentWasKept true .
	}

    PREFIX iot: <http://campus.guide/OpenIoT/owl#>
    DELETE DATA
	{
		<%s> iot:appointmentWasKept false .
	}
